mapper.insert:
insert into
	csreply(
		replynum
		,boardnum
		,id
		,text
	) values (
		csreply_seq.nextval
		,#{boardnum}
		,#{id}
		,#{text}
	)

mapper.delete:
delete from
	csboard
where
	boardnum = #{boardnum}
and
	id = #{id}

mapper.update:
update
	csboard
set
	title = #{title}
	,content = #{content}
	
	,originalfile = #{originalfile}
	,savedfile = #{savedfile}
			
where
	boardnum = #{boardnum}
and
	id = #{id}
