mapper.update:
update
Goods
set
goodsName = #{goodsName}
,goodsPrice = #{goodsPrice}
,goodsQuantity =#{goodsQuantity}

	,goodsOimage =#{goodsOimage}
	,goodsSimage = #{goodsSimage}

,goodsInfo =#{goodsInfo}
,goodsDateOfUpdate=sysdate
where
	goodsCode =#{goodsCode}
and
	sellerCRN=#{sellerCRN}

update 
	goods
set
	goodsQuantity = #{0}
where
	goodsCode = #{1}

update 
	goods
set
	goodsStatus = #{1}
where
	goodsCode = #{0}
